Axiom and conversion rules:


    \usepackage {bussproofs, amsopn, amssymb, mathrsfs, amsmath}
    \newenvironment {bprooftree}
      {\leavevmode \hbox \bgroup }
      {\DisplayProof \egroup }

    \begin {bprooftree}
      \AxiomC {}
      \LeftLabel {\small  (ax)}
      \RightLabel {\small {$\phi  \in  \Gamma , \mathcal {H} \vdash  \Gamma $}}
      \UnaryInfC {$\mathcal {H} ; \Gamma  \vdash  \phi $}
    \end {bprooftree}
    \begin {bprooftree}
      \AxiomC {$\mathcal {H} ; \Gamma  \vdash  \phi $}
      \LeftLabel {\small  (conv)}
      \RightLabel {\small {$\phi  =_\beta  \psi  , \mathcal {H} \vdash  \psi $}}
      \UnaryInfC {$\mathcal {H} ; \Gamma  \vdash  \psi $}
    \end {bprooftree}




  Logical fragment:


    \usepackage {bussproofs, amsopn, amssymb, mathrsfs, amsmath}
    \newenvironment {bprooftree}
      {\leavevmode \hbox \bgroup }
      {\DisplayProof \egroup }

    \begin {bprooftree}
      \AxiomC {$\mathcal {H} ; \Gamma  , \phi  \vdash  \psi $}
      \LeftLabel {\small  (imp-intro)}
      \UnaryInfC {$\mathcal {H} ; \Gamma  \vdash  \phi  \Rightarrow  \psi $}
    \end {bprooftree}
    \begin {bprooftree}
      \AxiomC {$\mathcal {H} ; \Gamma  \vdash  \phi  \Rightarrow  \psi $}
      \AxiomC {$\mathcal {H} ; \Gamma  \vdash  \phi $}
      \LeftLabel {\small  (impl-elim)}
      \BinaryInfC {$\mathcal {H} ; \Gamma  \vdash  \psi $}
    \end {bprooftree}






    \usepackage {bussproofs, amsopn, amssymb, mathrsfs, amsmath}
    \newenvironment {bprooftree}
      {\leavevmode \hbox \bgroup }
      {\DisplayProof \egroup }

    \begin {bprooftree}
      \AxiomC {$\mathcal {H}, x^\star  : T ; \Gamma  \vdash  \phi $}
      \LeftLabel {\small  (forall-intro)}
      \RightLabel {\small {$x \notin  \text {fv}(\Gamma )$}}
      \UnaryInfC {$\mathcal {H} ; \Gamma  \vdash  \forall (x : T).\phi $}
    \end {bprooftree}
    \begin {bprooftree}
      \AxiomC {$\mathcal {H} ; \Gamma  \vdash  \forall (x : T).\phi $}
      \LeftLabel {\small  (forall-elim)}
      \RightLabel {\small {$\mathcal {H} \vdash  t : T$}}
      \UnaryInfC {$\mathcal {H} ; \Gamma  \vdash  \phi [t/x]$}
    \end {bprooftree}




  Fixed point rules:


    \usepackage {bussproofs, amsopn, amssymb, mathrsfs, amsmath}
    \newenvironment {bprooftree}
      {\leavevmode \hbox \bgroup }
      {\DisplayProof \egroup }

    \begin {bprooftree}
      \AxiomC {$\mathcal {H} ; \Gamma  \vdash  (\mu ^\alpha  (x : T). \phi )\ \vec {\psi }$}
      \LeftLabel {\small  (unfold)}
      \RightLabel {\small {$\beta  \notin  \text {fv}((\mu ^\alpha  (x : T). \phi )\ \vec {\psi })$}}
      \UnaryInfC {$\mathcal {H} ; \Gamma  \vdash  \exists \beta  < \alpha . \phi [\mu ^\beta  (x : T). \phi  / x]\ \vec {\psi }$}
    \end {bprooftree}






    \usepackage {bussproofs, amsopn, amssymb, mathrsfs, amsmath}
