This system is used to carve out the "well-varianced" formulae from all those allowed by the definition of \mu HOL^{ex} syntax.


    \usepackage {bussproofs, amsopn, amssymb, mathrsfs, amsmath}
    \newcommand {\tctx }[0]{\mathcal {H}}
    \newenvironment {bprooftree}
      {\leavevmode \hbox \bgroup }
      {\DisplayProof \egroup }

    \begin {bprooftree}
      \AxiomC {}
      \LeftLabel {\small  (var)}
      \UnaryInfC {$\tctx , x^v : T \vdash  x : T$\ \ \ if $v\in  \{0,+\}$}
    \end {bprooftree}
    \begin {bprooftree}
      \AxiomC {$\tctx ^-\vdash  t : \Omega $}
      \AxiomC {$\tctx  \vdash  t' : \Omega $}
      \LeftLabel {\small  ($\Rightarrow $)}
      \BinaryInfC {$\tctx  \vdash  t \Rightarrow  t' : \Omega $}
    \end {bprooftree}






    \usepackage {bussproofs, amsopn, amssymb, mathrsfs, amsmath}
    \newcommand {\tctx }[0]{\mathcal {H}}
    \newenvironment {bprooftree}
      {\leavevmode \hbox \bgroup }
      {\DisplayProof \egroup }

    \begin {bprooftree}
      \AxiomC {$\tctx , x^0 : T \vdash  t : \Omega $}
      \LeftLabel {\small  ($\forall $)}
      \UnaryInfC {$\tctx  \vdash  \forall  (x : T).t : \Omega $}
    \end {bprooftree}

    \begin {bprooftree}
      \AxiomC {$\tctx , x^+ : P \vdash  t : P$}
      \LeftLabel {\small  ($\mu ^\alpha $)}
      \UnaryInfC {$\tctx  \vdash  \mu ^\alpha  (x : P).t : P$}
    \end {bprooftree}






