    \usepackage {bussproofs, amsopn, amssymb, mathrsfs, amsmath}
    \newcommand {\tctx }[0]{\mathcal {H}}
    \newenvironment {bprooftree}
      {\leavevmode \hbox \bgroup }
      {\DisplayProof \egroup }

    \begin {bprooftree}
      \AxiomC {$\tctx  \vdash  t : \Omega $}
      \LeftLabel {\small  ($\exists \alpha $)}
      \UnaryInfC {$\tctx  \vdash  \exists \alpha .t : \Omega $}
    \end {bprooftree}
    \begin {bprooftree}
      \AxiomC {}
      \LeftLabel {\small  ($<$)}
      \UnaryInfC {$\tctx  \vdash  \beta  < \alpha  : \Omega $}
    \end {bprooftree}




